import React from 'react';
import {Form, Row, Col, Button} from "react-bootstrap";
import DatePicker from "react-datepicker";
import es from "date-fns/locale/es";
import "../../../scss/UpdateUser.scss";
const UpdateUser = (props) => {
    const {user, setShow} = props;
    const onSubmit = e =>{
        e.preventDefault();
        console.log(user);
    }
    return (
        <div className="update-user">
            <Form onSubmit={onSubmit}>
                <Form.Group>
                    <Row>
                        <Col>
                            <Form.Control name="nombre" placeholder="Nombres" type="input" />
                        </Col>
                        <Col>
                            <Form.Control name="apellido" placeholder="Apellidos" type="input" />
                        </Col>
                    </Row>
                </Form.Group>
                <Form.Group>
                    <Form.Control as="textarea" placeholder="Ingresa tu biograÃ­a" type="input" row="3" name="biografia" />
                </Form.Group>
                <Form.Group>
                    <Form.Control name="sitioweb" placeholder="Sitio web" type="input" />
                </Form.Group>
                <Form.Group>
                    <DatePicker placeholder="Fecha de Nacimiento" locale={es} selected={new Date()}  />
                </Form.Group>
                <Button className="btn-submit" variant="primary" type="submit">Actualizar</Button>
            </Form>
        </div>
    );
}
export default UpdateUser;
function initForm(user){
    return {
        nombre: user.nobre || "",
        apellido: user.apellido || "",
        biografia: user.biografia || "",
        sitioweb: user.sitioweb || "",
        fechanac: user.fechanac || ""
    }
}
