import React, {useState, useEffect} from 'react';
import {Button, Spinner} from "react-bootstrap";
import BasicLayout from "../../../layout/BasicLayout";
import {withRouter} from "react-router-dom";
import {toast} from "react-toastify";
import {ProfileApi} from "../../../api/ProfileAPI";
import "../../../scss/Profile.scss";
const Profile = (props) => {
    const {match} = props;
    const {params} = match;
    const [user, setUser] = useState(defaultUser);
    useEffect(() => {
        ProfileApi(params.id).then(response=>{
            if(!response)
                toast.error("No se ha podido encontrar el perfil del usuario");
            setUser(response);
        })
        .catch(error=> toast.error("Ha ocurrido un error, intente más tarde"));
    },[params])
    return (
        <BasicLayout className="profile">
            <div className="profile-title">
                <h3>{user.nombre} {user.apellido}</h3>
            </div>
            <div>Banner usuario</div>
            <div>Lista de Usuarios</div>
            <div>Tweets</div>
        </BasicLayout>
    );
}
function defaultUser(){
    return {
        apellido: "",
        avatar: "",
        banner: "",
        biografia: "",
        email: "",
        fechanac: "",
        id: "",
        nombre: "Usuario no Existe",
        sitioweb: "",
        ubicacion: ""
    }
}
export default withRouter(Profile);
