import React,{useState} from 'react';
import {Button, Form, Spinner} from 'react-bootstrap';
import '../scss/Login.scss';
const Login = (props) => {
    const {setShow} = props;
    const [spinner, setSpinner] = useState(false);
    const [data, setData] = useState(defaultData)
    const onSubmit = e => {
        e.preventDefault();
        console.log(data);
    };
    const onChange = e =>{
        setData({...data, [e.target.name]: e.target.value});
    }
    return (
        <div className="login">
            <h3>Iniciar Sesi贸n</h3>
            <Form onSubmit={onSubmit} onChange={onChange} >
                <Form.Group>
                    <Form.Control name="email" defaultValue={data.email}
                    type="email" placeholder="Correo Electr贸nico"></Form.Control>
                </Form.Group>
                <Form.Group>
                    <Form.Control name="password" defaultValue={data.password}
                    type="password" placeholder="Password"></Form.Control>
                </Form.Group>
                <Button type="Submit" variant="primary">
                    {!spinner ? "Iniciar Sesi贸n" : <Spinner animation="border" /> }
                </Button>
            </Form>
        </div>
    );
}
export default Login;
function defaultData(){
    return {
        email: "",
        password: ""
    }
}
