import React, {useState, useEffect} from 'react';
import {toast} from "react-toastify";
import {Spinner, Button, ButtonGroup} from "react-bootstrap";
import BasicLayout from "../../../layout/BasicLayout";
import {getFollowers} from "../../../api/Follow";
import "../../../scss/Users.scss";
const Users = (props) => {
    const {setCheckLogin} = props;
    const [users, setUsers] = useState(null);
    useEffect(() => {
        getFollowers("type=follow&page=1&search=").then(response=>{
            console.log(response);
        })
        .catch(()=>toast.error("Error al cargar los usuarios"));
    },[])
    return (
        <BasicLayout className="users" setCheckLogin={setCheckLogin} >
            <div className="users-title">
                <h2>Usuarios</h2>
                <input type="text" placeholder="Buscar usuarios" />
            </div>
            <ButtonGroup className="users-options">
                <Button>Siguiendo</Button>
                <Button>Seguir</Button>
            </ButtonGroup>
        </BasicLayout>
    );
}
export default Users;
