import React, {useState} from 'react';
import BasicModal from "../components/modal/BasicModal";
import {Container, Row, Col, Button} from "react-bootstrap";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faSearch, faUsers, faComment} from "@fortawesome/free-solid-svg-icons"
import logo from "../Assets/logo.png";
import logoWhite from "../Assets/logoWhite.png";
import "../scss/Sign.scss";
const Sign = () => {
    const [show, setShow] = useState(false);
    const [content, setContent] = useState(null);
    return ( 
        <>
            <Container className="sign" fluid>
                <Row>
                    <LeftComponent />
                    <RightComponent />
                </Row>
            </Container>
            <BasicModal show={show} setShow={setShow}>
                <div>
                    <h2>Modal Content</h2>
                </div>
            </BasicModal>
        </>
    );
}
export default Sign;
function LeftComponent(){
    return (
        <Col className="signLeft" xs={6}>
            <img src={logo} alt="Logo Twiiter" />
            <div>
            <h4>
                <FontAwesomeIcon icon={faSearch}/>
                Sigue lo que te interesa
            </h4>
            <h4>
                <FontAwesomeIcon icon={faUsers}/>
                Entérate de que esta hablando la gente
            </h4>
            <h4>
            <FontAwesomeIcon icon={faComment}/>
                Únete a la conversación
            </h4>
            </div>
        </Col>
    );
}
function RightComponent(){
    return (
        <Col className="signRight" xs={6}>
            <div>
                <img src={logoWhite} alt="Logo blanco" />
                <h5>Mira lo que esta padando en este momento</h5>
                <h6>Registrate ahora mismo</h6>
                <Button variant="primary">Regístrate</Button>
                <Button variant="outline-primary">Iniciar Sesión</Button>
            </div>
        </Col>
    );
}
